// Generate worksheet with 10 random questions from the difficulty level
export function generateWorksheet(
  subject: SubjectType,
  difficulty: DifficultyLevel
): WorksheetQuestion[] {
  const questionBank: Record<string, any> = {
    'Math': MATH_QUESTIONS,
    'Science': SCIENCE_QUESTIONS,
    'History': HISTORY_QUESTIONS,
    'Bible': BIBLE_QUESTIONS,
    'Language Arts': LANGUAGE_ARTS_QUESTIONS,
  };

  const subjectQuestions = questionBank[subject];
  if (!subjectQuestions || !subjectQuestions[difficulty]) {
    console.error(`No questions found for ${subject} at ${difficulty} level`);
    return [];
  }

  const questions = subjectQuestions[difficulty];

  // Shuffle and take 10 questions
  const shuffled = [...questions].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, 10);
}

// Calculate stars based on score percentage
export function calculateStars(scorePercentage: number): number {
  if (scorePercentage >= 90) return 5;
  if (scorePercentage >= 80) return 4;
  if (scorePercentage >= 70) return 3;
  if (scorePercentage >= 60) return 2;
  if (scorePercentage >= 50) return 1;
  return 0;
}
